// ---------------------------- Burger Menu

$(function() {
  $('.burger-btn').on('click', function() {
    $('.menu').slideToggle();
    let $img = $('.burger-btn img');
    if ($img.attr('src') === 'images/burger-btn.png') {
      $img.attr('src', 'images/crest-btn.png');
      $img.attr('alt', 'crest menu button');
    } else {
      $img.attr('src', 'images/burger-btn.png');
      $img.attr('alt', 'burger menu button');
    }
  });
});

$(function() {
  $('.burger-btn-extra').on('click',function() {
    $('.menu').slideToggle();
    let $img = $(this).find('img');
    if ($img.attr('src') === '../images/burger-btn.png') {
      $img.attr('src', '../images/crest-btn.png');
      $img.attr('alt', 'crest menu button');
    } else {
      $img.attr('src', '../images/burger-btn.png');
      $img.attr('alt', 'burger menu button');
    }
  });
});

