// ---------------------------- Create Catalogue Cards

  $(function(){
    let maxCards = $(window).width() > 880 ? 15 : 10;
    $.each(catalogue, function (index, item){
    if (index >=0 && index < maxCards){
      if ($(window).width() > 880) {
      $('#catalogue-cards').append(`<li>
      <div class='img-holder'>
        <a href='./product/${item.ID}.html'><img src='${item.image}' alt='${item.imageAlt}' class='item-pic'></a>
        <button type='button' name='to-favorite' class='item-icon'></button>
      </div>
      <h4>
      ${item.itemName}
      </h4>
      <p>
      ${item.price} грн.
      </p>
      <div class='item-extra'>
        <a href='${item.description}'>Детальніше</a>
        <div class='tobasket-btn-container'>
          <button type='button' name='button'>В кошик</button>
        </div>
      </div>
    </li>`);
    }
    else {
    $('#catalogue-cards').append(`<li>
      <div class='img-holder'>
      <a href='./product/${item.ID}.html'><img src='${item.image}' alt='${item.imageAlt}' class='item-pic'></a>
      <button type='button' name='to-favorite' class='item-icon'></button>
      </div>
      <h4>
        ${item.itemName}
      </h4>
      <div class='mobile-extra'>
        <p>
          ${item.price} грн.
        </p>
        <button type='button' name='button' class='basket-icon'><img src='../images/icon_basket.png'></img></button>
      </div>
    </li>`);
    }
  }
  }
  )
})

